### 选项卡嵌套二级
#### HTML代码
    <!DOCTYPE html>
    <html lang="en">
    <head>
      <meta charset="UTF-8">
      <meta name="viewport" content="width=device-width, initial-scale=1.0">
      <meta http-equiv="X-UA-Compatible" content="ie=edge">
      <title>选项卡</title>
      <script src="index.js"></script>
      <style>
        * {
      margin: 0;
      padding: 0
    }

    li {
      list-style: none;
      width: 200px;
      height: 100px;
      text-algin: center;
      line-height: 100px;
    }
    ul {
      padding:0;
      margin:0;
    }
    li:hover {
      background: lightblue
    }

    li a {
      text-decoration: none;
      color: #8c9191;
      padding-left: 50px;
    }

    img {
      display: none;
      float: left;
    }

    #wrap {
      width: 800px;
      height: 400px;
      margin: 30px auto;
      border: 1px solid #dbdbdb;
      box-shadow: rgb(164, 160, 157) 1px 1px 9px -3px
    }

    .top {
      width: 300px;
      height: 30px;
      margin: 20px 0 0 20px;
    }

    #wrap span {
      display: inline-block;
      width: 100px;
      height: 30px;
      line-height: 30px;
      text-align: center;
      float: left;
      border: 1px solid #dbdbdb;
      box-shadow: rgb(164, 160, 157) 1px 1px 9px -3px
    }

    .wrap span:hover {
      background: lightblue
    }
    .left {
      float:left;
      width: 534px;
      height: 300px;
    }
    .bottom {
      height: 300px;
      margin: 20px
    }

    .right {
      width: 200px;
      height: 300px;
      margin: 0 0 0 20px;
      float:right
    }

    .active {
      background: #636363;
      display: block;
    }

    .active a {
      color: #fff;
    }

    .show {
      display: block;
    }

    .hide {
      display: none;
    }
      </style>
    </head>
    <body>
      <div id="wrap">
        <div class="top">
          <span>专题一</span>
          <span>专题二</span>
        </div>
        <div class="bottom show">
          <div class="left">
            <img class="active" src="img/0.jpg" alt="">
            <img class="" src="img/1.jpg" alt="">
            <img class="" src="img/2.jpg" alt="">
          </div>
            <ul class="right">
              <li class=""><a href="javascript:;">test picture 1</a></li>
              <li><a href="javascript:;">test picture 2</a></li>
              <li><a href="javascript:;">test picture 3</a></li>
            </ul>
        </div>
        <div class="bottom hide">
          <div class="left">
            <img class="active" src="img/3.jpg" alt="">
            <img class="" src="img/4.jpg" alt="">
            <img class="" src="img/5.jpg" alt="">
          </div>
            <ul class="right">
              <li class=""><a href="javascript:;">test picture 4</a></li>
              <li><a href="javascript:;">test picture 5</a></li>
              <li><a href="javascript:;">test picture 6</a></li>
            </ul>
        </div>
      </div>
    </body>
    </html>

#### javascript代码
    window.onload = function(){
      let timer = 0;
      let wrap = document.getElementById('wrap');
      let title = document.getElementsByTagName('span');
      let bottom = document.querySelectorAll('.bottom');
      let imgs = bottom[0].querySelectorAll('img');
      let text = bottom[0].querySelectorAll('li');

      text[0].style.backgroundColor = 'lightblue';
      title[0].style.backgroundColor = 'lightblue';

      let a = 0;
      let num = 0;

      fn();
      function fn(){
        clearInterval(timer);
        timer = setInterval(function(){
          imgs[a].className = '';
          text[a].style.background = '';
          title[num].style.background = '';
          a++;
          if(a > text.length-1){
            a = 0;
            bottom[num].style.display = 'none';
            num++
          }
          if(num > bottom.length-1){
            num = 0
          }
          text = bottom[num].querySelectorAll('li');
          imgs = bottom[num].querySelectorAll('img');
          imgs[a].className = 'active';
          text[a].style.backgroundColor = 'lightblue';
          bottom[num].style.display = 'block';
          title[num].style.background = 'lightblue';
        },1500)
      }

      for(let i=0;i<title.length;i++){
          title[i].index = i;
          title[i].onmouseover = function(){
              a=0;
              for(let i=0;i<title.length;i++){
                  text = bottom[i].querySelectorAll('li');
                  imgs = bottom[i].querySelectorAll('img');
                  for(let j=0;j<imgs.length;j++){
                      imgs[j].className = '';
                      text[j].style.backgroundColor='';
                  };
              };
              text[a].style.backgroundColor = '';
              bottom[num].style.display='none';
              title[num].style.backgroundColor='';

              num = this.index;
              bottom[num].style.display = 'block';
              imgs = bottom[num].querySelectorAll('img');
              imgs[a].className = 'active';

              title[num].style.background = 'lightblue';

              text = bottom[num].querySelectorAll('li');
              text[a].style.backgroundColor='lightblue';
          };
      }

      for(let k=0;k<bottom.length;k++){

          bottom[k].onmouseover = function(){
              text = this.querySelectorAll('li');
              imgs = this.querySelectorAll('img');
              for(let i=0;i<text.length;i++){

                  text[i].index = i;
                  text[i].onmouseover = function(){
                      for(let i=0;i<text.length;i++){
                          text[i].style.background = '';
                          imgs[i].className = '';
                      }
                      imgs[this.index].className = 'active';
                      this.style.background = 'lightblue';
                  };
                  text[i].onmouseout = function(){
                      a = this.index;
                  };
              }
          }
      }

      wrap.onmouseover = function(){
          clearInterval(timer);
      }
      wrap.onmouseout = function(){
          fn();
      }

    };

