		var MyTodoList = (function(){
			var defaults ={   //这里我们定义一些常量，用于在js里使用，它们分别是html中的类名以及id标识
				todoTask:"todo-task",
				taskHeader:"task-header",
				taskDate:"task-date",
				taskDescription:"task-description",
				taskID:"task-",
				formID:"todo-form",
				deleteDiv:"delete-div"
			},
			codes = {  //这里我们用三个数字分别代替三个区块，即让数字索引它们的id
				"1":"#pending",
				"2":"#inprogress",
				"3":"#completed"
			};
			var MyTodoList =function(){

			};

			MyTodoList.prototype.generateElement = function(params){ //定义一个prototype function
				var parent = $(codes[params.code]), //这里获取我们传入的参数让我们往哪个区块去添加，即找这个区域的父区域
				wrapper;

				if(!parent){
					return;
				}

				wrapper = $('<div />',{  定义这个区域
					"class":defaults.todoTask,
					"id":defaults.taskID+params.id,
					"data":params.id
				});

				$('<div />',{  //定义它里面的标题小div
					"class":defaults.taskHeader,
					"text":params.title

				}).appendTo(wrapper); //把他插入到wrapper里
				$('<div />',{  //定义它里面的时间小div
					"class":defaults.taskDate,
					"text":params.date
				}).appendTo(wrapper);

				$('<div />',{  //定义它里面的内容小div
					"class":defaults.taskDescription,
					"text":params.description
				}).appendTo(wrapper);
				wrapper.appendTo(parent);  //最后将创建好的整个div加到需要加的区域里
			};
			return MyTodoList;
		})()

		==重点是app.js里的代码==

		## 第三步：实现添加删除以及按钮的绑定
		![](https://img-blog.csdnimg.cn/20181219191316206.png?x-oss-process=image/watermark,type_ZmFuZ3poZW5naGVpdGk,shadow_10,text_aHR0cHM6Ly9ibG9nLmNzZG4ubmV0L3dlaXhpbl80MjUwNzc1Ng==,size_16,color_FFFFFF,t_70)
