        <script src="js.js/jquery-ui.js"></script>
        <script type="text/javascript">
            $(document).ready(function(){
    			 $('.block').draggable();
                 $('.drop').droppable({
                      accept: '.block',
                      activeClass: 'droppable-active',
                      hoverClass: 'droppable-hover',
                      drop: function(ev, ui) {
                          $(this).append('<br>Dropped!');
                      }
                  });
            });
